const intercept = -2.1804156327512527;
const coefficients = new Float32Array([0.22726018506138604,-0.673867117263062,0.6316235060865015]);

const x = intercept +
          coefficients[0] * msg.payload.d["AccelerometerX@StarterSensor"] +
          coefficients[1] * msg.payload.d["AccelerometerY@StarterSensor"] +
          coefficients[2] * msg.payload.d["AccelerometerZ@StarterSensor"] ;

msg.payload = 100.0 / (1.0 + Math.exp(-x));

return msg;
